let like=document.querySelectorAll(".goods__item-like");like.forEach(e=>{e.addEventListener("click",()=>{e.classList.toggle("active")})});let btn=document.querySelectorAll(".goods__item-btn");btn.forEach(t=>{t.addEventListener("click",e=>{btns=e.target.closest(".goods__item").querySelector(".goods__item-btns"),btns.classList.add("active"),t.classList.add("hidden")})});let count=1,plus=document.querySelectorAll(".goods__item-btns_plus"),minus=document.querySelectorAll(".goods__item-btns_minus");plus.forEach(e=>{e.addEventListener("click",e=>{count++,cnt=e.target.closest(".goods__item").querySelector(".goods__item-btns_count"),cnt.innerHTML=count+" шт",console.log(count)})}),minus.forEach(e=>{e.addEventListener("click",e=>{if(count--,0<count)cnt=e.target.closest(".goods__item").querySelector(".goods__item-btns_count"),cnt.innerHTML=count+" шт";else{btns.classList.remove("active");let e=btns.closest(".goods__item").querySelector(".goods__item-btn");e.classList.remove("hidden")}})});